mapper.select:
SELECT	*
FROM	TB_WEATHER_INFO

	AND	ADMIN_ID LIKE #{admin_id}


	AND	ADMIN_GROUP_ID LIKE #{admin_group_id}

SELECT	*
FROM	TB_WEATHER_INFO
WHERE	1 = 1

	AND	ADMIN_ID LIKE #{admin_id}


	AND	ADMIN_GROUP_ID LIKE #{admin_group_id}

mapper.insert:
INSERT INTO TB_WEATHER_INFO
(
	ADMIN_ID
	,	ADMIN_GROUP_ID

		,	LCTN_NM

)
VALUES
(
	#{admin_id}
	,	#{admin_group_id}

		,	#{lctn_nm}

)

mapper.update:
	UPDATE	TB_WEATHER_INFO
	SET		LG_DTTM = now()

    	, LCTN_NM = #{lctn_nm}


		, WTHR_CD = #{wthr_cd}


		, WTHR_VL = #{wthr_vl}


		, DST_CD = #{dst_cd}


		, DST_VL = #{dst_vl}


		, CR_TMP = #{cr_tmp}


	WHERE	ADMIN_GROUP_ID = #{admin_group_id}

		AND	ADMIN_ID = #{admin_id}

mapper.delete:
DELETE
FROM	TB_WEATHER_INFO
WHERE	ADMIN_GROUP_ID = #{admin_group_id}
AND		ADMIN_ID = #{admin_id}
